mapper.update:
UPDATE MEMBER 
 MEMBER_Password =#{member_Password},
MEMBER_EMAIL = #{member_Email},
MEMBER_PHONENUMBER = #{member_PhoneNumber}

,MEMBER_PROFILE_BLOB = #{member_Profile_Blob}
WHERE MEMBER_ID = #{member_ID}

UPDATE MEMBER SET
MEMBER_ROLE  =#{member_Role}
WHERE MEMBER_ID = #{member_ID}
